export function TestimonialsSection() {
  const renderStars = (rating: number) => {
    return Array.from({ length: 5 }).map((_, index) => (
      <Star
        key={index}
        className={`w-4 h-4 ${
          index < rating ? 'fill-accent text-accent' : 'text-muted-foreground/30'
        }`}
      />
